import React, { useState } from "react";
import AppText from "../../../components/AppText";
import '../../../index.css';
import './LoginPage.css';
import logo from './logo.svg';

function LoginPage({ onLogin }) {
  const [name, setName] = useState("");
  const [password, setPassword] = useState("");

  function handleSubmit(event) {
    onLogin({ name, password });
  }
  const textContent = "Hello, this is the text content";


  return (
    <>
      <img src={logo} className="App-logo vibrating-rotating-element" alt="logo" />
      <form className="form" onSubmit={handleSubmit}>
        <label>
          <AppText theText={textContent}/>
          <input
            type="text"
            value={name}
            className="text-area"
            onChange={(e) => setName(e.target.value)}
          />
        </label>
        <br />
        <label>
          <p className="text-persian text-shadow">
            گذرواژه
          </p>
          <input
            type="password"
            value={password}
            className="text-area"
            onChange={(e) => setPassword(e.target.value)}
          />
        </label>
        <br />
        <input className="button" type="submit" value="ورود" />
      </form>
    </>
  );
}

export default LoginPage;
