import axios from 'axios';
import React, { useEffect, useState } from 'react';
import DeleteButton from '../../../../../../components/DeleteButton';
import Table from '../../../../../../components/Table';
import { api_address } from '../../../../../../config';

function ManageAccounts({ account }) {
  const [table, setTable] = useState({ columnNames: [], rows: [] });

  useEffect(() => {
    const key = { id: account.id, password: account.password };
    axios.post(`${api_address}/api/get-users`, { key })
      .then((res) => {
        const tableData = res.data;
        // Add a "Delete" button to each row
        const rowsWithButton = tableData.rows.map((row, index) => {
          return {
            ...row,
            action: <DeleteButton onClick={() => handleDelete(row.id)}>Delete</DeleteButton>
          };
        });
        setTable({ columnNames: tableData.columnNames, rows: rowsWithButton });
      })
      .catch(error => {
        console.error("Error fetching table data:", error);
      });
  }, [account.id, account.password]);

  const handleDelete = (id) => {
    // Handle the delete action here
    console.log("Deleting user with ID:", id);
  };

  return (
    <div>
      <Table columnNames={table.columnNames} rows={table.rows} />
    </div>
  );
}

export default ManageAccounts;
