import React, { useState } from 'react';
import Button from '../../../../components/Button';
import LabelText from '../../../../components/LabelText';

function AdminPanel() {
  const [control, setControl] = useState[''];

  function setCreateUserControl(event) {
    event.preventDefault();
    setControl('CREATE_USER');
  }

  function setCreateBillControl(event) {
    event.preventDefault();
    setControl('CREATE_BILL');
  }

  function setBillAssigningControl(event) {
    event.preventDefault();
    setControl('BILL_ASSIGNING');
  }

  function setPaymentControl(event) {
    event.preventDefault();
    setControl('PAYMENT');
  }

  return (
    <div className="navbar">
          < LabelText fontSize={'2rem'} fontWeight={'bold'} theText={'پنل کنترل'} />
          <Button onClick={setPaymentControl}><LabelText theText={'ثبت بدهی پرداخت شده'} /></Button>
          <Button onClick={setBillAssigningControl}><LabelText theText={'تخصیص صورت حساب موجود به کاربران'} /></Button>
          <Button onClick={setCreateBillControl}><LabelText theText={'ایجاد صورت حساب جدید'} /></Button>
          <Button onClick={setCreateUserControl}><LabelText theText={'ایجاد کاربر جدید'} /></Button>
    </div >
  )
}

export default AdminPanel
