import axios from 'axios';
import React, { useEffect, useState } from 'react';
import Table from '../../../../../../components/Table';
import { api_address } from '../../../../../../config';

function ManageAccounts({ account }) {
  const key = { id: account.id, password: account.password };
  const [table, setTable] = useState({ columnNames: [], rows: [] });

  useEffect(() => {
    const key = { id: account.id, password: account.password };
    axios.post(`${api_address}/api/get-users`, { key })
      .then((res) => {
        const table = res.data;
        setTable({ columnNames: table.columnNames, rows: table.rows });
        console.log(table)
      })
      .catch(error => {
        console.error("Error fetching table data:", error);
      });
  }, [account.id, account.password]);

  function handleDeleteAccount(id){
    axios.post(`${api_address}/api/delete-user`, { key, id })
      .then((res) => {
        const table = res.data;
        setTable({ columnNames: table.columnNames, rows: table.rows });
        console.log(table)
      })
      .catch(error => {
        console.error("Error fetching table data:", error);
      });
  }

  return (
    <div>
      <Table columnNames={table.columnNames} rows={table.rows} onDeleteButton={handleDeleteAccount}/>
    </div>
  );
}

export default ManageAccounts;
