import React, { useState } from 'react';
import Button from '../../../../components/Button';
import LabelText from '../../../../components/LabelText';
import BillAssignment from './AdminControls/BillAssignment';
import CreateBill from './AdminControls/CreateBill';
import CreateUser from './AdminControls/CreateUser';
import Payment from './AdminControls/Payment';

function AdminPanel() {
  const [controller, setController] = useState('');

  function setControlTo(control) {
    setController(control);
  }

  if (controller === 'CREATE_USER') {
    return (<CreateUser />)
  }
  else if (controller === 'CREATE_BILL') {
    return (<CreateBill />)
  }
  else if (controller === 'BILL_ASSIGNMENT') {
    return (<BillAssignment />)

  }
  else if (controller === 'PAYMENT') {
    return (<Payment />)

  }
  else {
    return (
      <div className="navbar">
        < LabelText fontSize={'2rem'} fontWeight={'bold'} theText={'پنل کنترل'} />
        <Button onClick={setControlTo('PAYMENT')}><LabelText theText={'ثبت بدهی پرداخت شده'} /></Button>
        <Button onClick={setControlTo('BILL_ASSIGNMENT')}><LabelText theText={'تخصیص صورت حساب موجود به کاربران'} /></Button>
        <Button onClick={setControlTo('CREATE_BILL')}><LabelText theText={'ایجاد صورت حساب جدید'} /></Button>
        <Button onClick={setControlTo('CREATE_USER')}><LabelText theText={'ایجاد کاربر جدید'} /></Button>
      </div >
    )
  }
}

export default AdminPanel
