import axios from 'axios';
import React, { useEffect, useState } from "react";
import { useCookies } from "react-cookie";
import LoginPage from "./LoginPage/LoginPage";
import UserPanel from "./UserPanel/UserPanel";

function Body() {
    const [cookies, setCookie, removeCookie] = useCookies(['account']);
    const [account, setAccount] = useState({});

    useEffect(() => {
        if (Object.keys(account).length > 0) {
            console.log("current value of 'account' state: ", account);
            setCookie('account', account);
        }
    }, [account, setCookie]);

    function handleLogin(user) {
        console.log(user);
        axios.post('http://localhost:3001/api/login', user)
            .then((res) => {
                console.log("received data from Login API: ", res.data);
                console.log("Type of that data: ", typeof res.data);
                setAccount(res.data);
                setCookie('account', res.data);
            })
            .catch(err => {
                console.error(err);
            });
    }

    function handleLogout() {
        removeCookie('account');
    }

    return (
        <>
            {cookies.account ? (
                <UserPanel account={cookies.account} onLogout={handleLogout} />
            ) : (
                <LoginPage onLogin={handleLogin} />
            )}
        </>
    );
}

export default Body;
