import axios from 'axios';
import React, { useState } from "react";
import { useCookies } from "react-cookie";
import LoginPage from "./LoginPage/LoginPage";
import UserPanel from "./UserPanel/UserPanel";

function Body() {
    const [cookies, setCookie, removeCookies] = useCookies(["account"]);
    const [account, setAccount] = useState("");

    function handleLogin(user) {
        /*
        axios.post('http://localhost:3001/api/login', user)
            .then((res) => {
                if (res.data != null) {
                    setAccount(res.data)
                    setCookie("account", account);
                }
            })
            .catch(err => {
                console.error(err);
            });
        */
       setAccount(axios.post('http://localhost:3001/api/login', user).then((res)=>resolve(res.data)))
       setCookie("account", JSON.parse(account))
    }

    function handleLogout(account) {
        removeCookies("account", account);
        setAccount(null);
    }

    return (
        <>
            {cookies.account ? (
                <UserPanel user={cookies.account} onLogout={handleLogout} />
            ) : (
                <LoginPage onLogin={handleLogin} />
            )}
        </>
    );
}

export default Body;
