import React, { useState } from 'react';
import LabelText from './LabelText';

function Form({ formData, handleSubmit }) {
  const [formValues, setFormValues] = useState({});

  const handleInputChange = (key, value) => {
    setFormValues({ ...formValues, [key]: value });
  };

  const onSubmit = (event) => {
    event.preventDefault();
    handleSubmit(formValues);
  };

  return (
    <form className="form" onSubmit={onSubmit} style={{ display: 'flex', alignItems: 'center' }}>
      {Object.keys(formData).map((key) => (
        <div key={key}>
          <label>
            <LabelText theText={formData[key]} />
            <input
              type="text"
              value={formValues[key] || ''}
              className="text-area"
              onChange={(e) => handleInputChange(key, e.target.value)}
            />
          </label>
          <br />
        </div>
      ))}
      <input style={{width:'10vw',height: '10vh'}} className="button" type="submit" value="ثبت" />
    </form>
  );
}

export default Form;
