import React, { useState } from 'react';
import Button from '../../../../components/Button';
import LabelText from '../../../../components/LabelText';

function AdminPanel() {
  const [panel, setPanel] = useState[""];

  function setCreateUser(event) {
    event.preventDefault();
    setPanel('CREATE_USER');
  }

  function setCreateBillPanel(event) {
    event.preventDefault();

  }

  function setBillAssignPanel(event) {
    event.preventDefault();

  }

  function setPaymentPanel(event) {
    event.preventDefault();

  }

  return (
    <div className="navbar">
      <LabelText fontSize={'2rem'} fontWeight={'bold'} theText={'پنل کنترل'} />
      <Button onClick={setPaymentSubmit}><LabelText theText={'ثبت بدهی پرداخت شده'} /></Button>
      <Button onClick={setBillAssign}><LabelText theText={'تخصیص صورت حساب موجود به کاربران'} /></Button>
      <Button onClick={setCreateBill}><LabelText theText={'ایجاد صورت حساب جدید'} /></Button>
      <Button onClick={setCreateUser}><LabelText theText={'ایجاد کاربر جدید'} /></Button>
    </div>
  )
}

export default AdminPanel
